var myApp=angular.module("myModule",[]).controller("myController",(function(e,o,t){e.job={},e.display1=!0,e.display2=!1,e.display3=!1,e.previousJobsByRecruiter=[],e.applicants=[],e.submitForm=function(){e.job.RecruiterId=document.getElementById("RecruiterId").value,t.post("/api/v1/jobs/createJob",e.job).then((function(o){alert(o.data.message),e.job.JobTitle="",e.job.JobDesc=""}),(function(e){alert("Some error occured: "+e)}))},e.previousJob=function(){var o="/api/v1/jobs/"+document.getElementById("RecruiterId").value;t.get(o).then((function(o){e.previousJobsByRecruiter=o.data.jobs,e.display1=!1,e.display2=!0,e.display3=!1}),(function(e){alert("Some error occured: "+e)}))},e.createJob=function(){e.previousJobsByRecruiter=[],e.applicants=[],e.display1=!0,e.display2=!1,e.display3=!1},e.checkApplicants=function(o){var a="/api/v1/candidates/"+o.JobId;t.get(a).then((function(o){e.applicants=o.data.candidates,e.display1=!1,e.display2=!1,e.display3=!0}),(function(e){alert("Some error occured: "+e)}))}}));